import axios from 'axios';

import { 
    GET_LOGS, 
    SET_LOADING, 
    LOGS_ERROR, 
    ADD_LOG, 
    DELETE_LOG, 
    // UPDATE_LOG,
    // SEARCH_LOGS, 
    // SET_CURRENT, 
    // CLEAR_CURRENT 
} from './types';

// Add new log to server with axios
export const addLog = (log) => async dispatch => {
    try {
        setLoading();

        const config = {
            headers: {
                'Content-Type': 'application/json'
            }
        }

        const res = await axios.post('/api/logs', log, config);

        dispatch({
            type: ADD_LOG,
            payload: res.data
        })
    } catch (err) {
        dispatch({
            type: LOGS_ERROR,
            payload: err.response.statusText
        })
    }
}

// Get logs from server
export const getLogs = () => async dispatch => {
    try {
        setLoading();

        const res = await axios.get('/api/logs', log, config);

        dispatch({
            type: GET_LOGS,
            payload: res.data
        })
    } catch (err) {
        dispatch({
            type: LOGS_ERROR,
            payload: err.response.statusText
        })
    }
};

// Add new log to server with axios
export const deleteLog = (log) => async dispatch => {
    try {
        setLoading();

        const config = {
            headers: {
                'Content-Type': 'application/json'
            }
        }

        const res = await axios.delete('/api/logs/:_id', log, config);

        dispatch({
            type: DELETE_LOG,
            payload: res.data
        })
    } catch (err) {
        dispatch({
            type: LOGS_ERROR,
            payload: err.response.statusText
        })
    }
}


// Set loading to true
export const setLoading = () => {
    return {
        type: SET_LOADING
    };
};
