import axios from 'axios';

import { 
    GET_LOGS, 
    SET_LOADING, 
    LOGS_ERROR, 
    ADD_LOG, 
    DELETE_LOG,
    SET_CURRENT, 
    // UPDATE_LOG,
    // SEARCH_LOGS, 
    CLEAR_CURRENT 
} from './types';

// Add new log to server with axios
export const addLog = (log) => async dispatch => {
    try {
        setLoading();

        const config = {
            headers: {
                'Content-Type': 'application/json'
            }
        }

        const res = await axios.post('/api/logs', log, config);

        dispatch({
            type: ADD_LOG,
            payload: res.data
        })
    } catch (err) {
        dispatch({
            type: LOGS_ERROR,
            payload: err.response.statusText
        })
    }
}

// Get logs from server
export const getLogs = (log) => async dispatch => {
    try {
        setLoading();

        const config = {
            headers: {
                'Content-Type': 'application/json'
            }
        }

        const res = await axios.get('/api/logs', log, config);

        dispatch({
            type: GET_LOGS,
            payload: res.data
        })
    } catch (err) {
        dispatch({
            type: LOGS_ERROR,
            payload: err.response.statusText
        })
    }
};

// Delete log to server with axios
export const deleteLog = (_id) => async dispatch => {
    try {
        setLoading();

        await axios.delete(`/api/logs/${_id}`);

        dispatch({
            type: DELETE_LOG,
            payload: _id
        })
    } catch (err) {
        dispatch({
            type: LOGS_ERROR,
            payload: err.response.statusText
        })
    }
}

// Set current log
export const setCurrent = log => {
    return {
        type: SET_CURRENT,
        payload: log
    }
}

// Clear current log
export const clearCurrent = () => {
    return {
        type: CLEAR_CURRENT,
    }
}

// Set loading to true
export const setLoading = () => {
    return {
        type: SET_LOADING
    };
};
